<script type="text/javascript">
	$('.events_menu_list').click(function(){
		$('.event_contents').hide();
		$('.events_menu_list').removeClass("events_menu_active");
		$(this).addClass("events_menu_active");
		tabId=($(this).attr('id')).substring(5);
		$('#content_'+tabId).fadeIn(400);
	});
</script>